import idb from 'idb'

export default class DBController {
  constructor() {
    this.db = idb
      .open('football-pwa', 1, upgradeDB => {
        upgradeDB.createObjectStore('competitions', { keyPath: 'id'})
        upgradeDB.createObjectStore('seasons', {keyPath: 'id'})
        upgradeDB.createObjectStore('matches', {keyPath: 'id', autoIncrement: true})
        upgradeDB.createObjectStore('standings', {keyPath: 'id', autoIncrement: true})
        upgradeDB.createObjectStore('teams', {keyPath: 'id'})
      })
  }

  saveData(data, objectStore) {
    this
      .db
      .then(db => {
        let tx = db.transaction(objectStore, 'readwrite')
        let store = tx.objectStore(objectStore)

        console.log(data)
        store.add(data)

        return tx.complete
      })
      .then(() => console.log('Berhasil simpan data ke database IndexedDB'))
      .catch(error => console.error(`IndexedDB: gagal simpan data \n${error}`))
  }

  getAllData(objectStore) {
    return new Promise((resolve, reject) => {
      this
        .db
        .then(db => {
          let tx = db.transaction(objectStore, 'readwrite')
          let store = tx.objectStore(objectStore)

          return store.getAll()
        })
        .then(data => resolve(data))
        .catch(error => reject(error))
    })
  }

  getDataById(id, objectStore) {
    return new Promise((resolve, reject) => {
      this
        .db
        .then(db => {
          let tx = db.transaction(objectStore, 'readwrite')
          let store = tx.objectStore(objectStore)

          return store.get(id)
        })
        .then(data => resolve(data))
        .catch(error => reject(error))
    })
  }

  deleteData(id, objectStore) {
    this
      .db
      .then(db => {
        let tx = db.transaction(objectStore, 'readwrite')
        let store = tx.objectStore(objectStore)

        store.delete(id)
        return tx.complete
      })
      .then(status => console.log(`IndexedDB: berhasil menghapus data \n${status}`))
      .catch(error => console.log(error))
  }
}
